INSERT INTO `reversion_version` VALUES(2298,'148',148,'json','[{"fields": {"content": "<h2 style=\"font-style:italic\"><span style=\"color:#0000FF\">\u4f01\u4e1a\u7b80\u4ecb</span></h2>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp; \u6cb3\u5317\u84dd\u7bad\u6a61\u80f6\u673a\u5e26\u6709\u9650\u516c\u53f8\u662f\u4ee5\u751f\u4ea7&ldquo;\u84dd\u7bad&rdquo;\u724c\u7cfb\u5217\u8f93\u9001\u5e26\u4e3a\u4e3b\u5bfc\u7684\u4e2d\u578b\u80f6\u5e26\u4f01\u4e1a\uff0c\u662f\u56fd\u5bb6IS09001\u8ba4\u8bc1\u5355\u4f4d\u3002\u5ea7\u843d\u4e8e\u4fdd\u5b9a\u5e02\u535a\u91ce\u6a61\u80f6\u5de5\u4e1a\u56ed\u533a\u3002\u7d27\u90bb\u7684\u5b9a\u6ca7\u516c\u8def\u4e0a\uff0c\u901a\u5f80\u5929\u6d25\u3001\u77f3\u5bb6\u5e84\u7684\u8f66\u8f86\u5ddd\u6d41\u4e0d\u606f\u5e76\u4e14\u524d\u4fef\u6714\u9ec4\u94c1\u8def\uff0e\u4ea4\u901a\u4fbf\u5229\uff0c\u56db\u901a\u516b\u8fbe\u3002<br />\r\n&nbsp;&nbsp;&nbsp; \u516c\u53f8\u81ea\u521b\u5efa\u4ee5\u6765\u59cb\u7ec8\u575a\u6301&ldquo;\u5f00\u62d3\u521b\u65b0\u3001\u7cbe\u76ca\u6c42\u7cbe&rdquo;\u7684\u4f01\u4e1a\u7cbe\u795e\uff0c\u4e00\u5207\u4ece\u5e02\u573a\u9700\u6c42\u51fa\u53d1\uff0c\u4f9d\u9760\u79d1\u6280\u529b\u91cf\u4e0e\u5148\u8fdb\u8bbe\u5907\uff0c\u751f\u4ea7\u51fa\u4e86\u8ba9\u7528\u6237\u6ee1\u610f\u7684\u4ea7\u54c1\u3002\u6211\u516c\u53f8\u4e3b\u8981\u4ea7\u54c1 \u6709\uff1aB1600MM\u4ee5\u4e0b\u666e\u901a\u578b\u8f93\u9001\u5e26\u3001\u8010\u70ed\u8f93\u9001\u5e26\u3001PVC. PVG\u963b\u71c3\u8f93\u9001\u5e26\u3001\u7b52\u72b6\u8f93\u9001\u5e26\u3001\u73af\u5f62\u8f93\u9001\u5e26\u3001\u5c3c\u9f99\u3001\u805a\u916f\u8f93\u9001\u5e26\u3001\u82b1\u7eb9\u8f93\u9001\u5e26\u3001\u8010\u6cb9\u8f93\u9001\u5e26\u3001\u94a2\u4e1d\u9632\u6495\u88c2\u8f93\u9001\u5e26\u3001\u87ba\u65cb\u7f51\u8f93\u9001\u5e26\u7b49\u3002\u516c\u53f8\u68c0\u6d4b\u624b\u6bb5\u5b8c\u5907\uff0c\u5177\u6709\u72ec\u7acb \u7684\u5316\u5b66\u5206\u6790\u5ba4\u3001\u7269\u7406\u5b9e\u9a8c\u5ba4\uff0c\u4fdd\u8bc1\u4e86\u516c\u53f8\u4f7f\u7528\u7684\u539f\u6750\u6599\u3001\u534a\u6210\u54c1\u53ca\u4ea7\u54c1\u90fd\u5904\u4e8e\u53d7\u63a7\u72b6\u6001\uff0c\u786e\u4fdd\u4e86\u4e0d\u5408\u683c\u539f\u6599\u4e0d\u8fdb\u5382\uff0c\u4e0d\u5408\u683c\u4ea7\u54c1\u4e0d\u51fa\u5382\u3002\u6211\u516c\u53f8\u4ea7\u54c1\u5df1\u5b8c\u6210&ldquo;\u53cc \u91c7&rdquo;\uff0c\u5e76\u5168\u90e8\u6309\u56fd\u5bb6\u6807\u51c6( GB/T7984-2001)\u8fdb\u884c\u751f\u4ea7\uff0c\u516c\u53f8\u4ea7\u54c1\u8fde\u7eed\u51e0\u5e74\u5728\u6cb3\u5317\u7701\u4ea7\u54c1\u76d1\u7763\u68c0\u9a8c\u6240\u62bd\u6837\u68c0\u9a8c\u5168\u90e8\u5408\u683c\u3002\u4ea7\u54c1\u7545\u9500\u56fd\u518526\u4e2a\u7701\u3001\u5e02\u3001\u81ea\u6cbb\u533a\u7684\u51e0\u767e\u5bb6\u51b6\u91d1\u3001\u94a2 \u94c1\u3001\u7164\u70ad\u3001\u5efa\u6750\u3001\u5316\u5de5\u3001\u7cae\u98df\u7b49\u884c\u4e1a\uff0c\u6df1\u53d7\u7528\u6237\u7684\u4fe1\u8d56\u548c\u597d\u8bc4\u3002<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; \u6211\u516c\u53f8\u4e00\u8d2f\u575a\u6301\u4ee5\u65b0\u53d6\u80dc\u3001\u4f18\u5316\u4ea7\u54c1\u7ed3\u6784\uff0c\u5f3a\u5316\u57fa\u7840\u7ba1\u7406\uff0c\u62d3\u5bbd\u8425\u9500\u5e02\u573a\uff0c\u589e\u5f3a\u4f01\u5317\u6d3b\u529b\u3002\u5982\u4eca\u968f\u7740\u5165\u4e16\u6210\u529f\uff0c\u7ed9\u4f01\u4e1a\u63d0\u4f9b\u4e86\u66f4\u52a0\u5e7f\u9614\u7684\u53d1\u5c55\u7a7a\u95f4\u3002\u6240\u6709\u84dd\u7bad\u4eba\u6b63\u4ee5\u575a\u5b9a\u7684\u4fe1\u5fc3\u548c\u6602\u626c\u7684\u6597\u5fd7\u5728\u53d1\u5c55\u7684\u9053\u8def\u4e0a\u8de8\u6b65\u5411\u524d\u3002</p>\r\n\r\n<p><iframe class=\"igtranslator-iframe\" frameborder=\"0\" scrolling=\"no\" src=\"about:blank\" style=\"display: none; width: 0px; height: 0px;\"></iframe></p>\r\n\r\n<div class=\"igtranslator-activator-icon\" style=\"background-image: url(&quot;resource://jid1-dgnibwqga0sibw-at-jetpack/igtranslator/data/content_script/icon.png&quot;); display: none;\" title=\"Click to Show Translation\">&nbsp;</div>\r\n"}, "model": "richtext.richtext", "pk": 148}]','148',41,232);
